export default function Recording() {

  const [rec, setRec] = useState({
            url: null,
            blob: null,
            chunks: null,
            duration: {
              h: 0,
              m: 0,
              s: 0
          }
  });

  const handleAudioStop = (data) => {
    setRec({ rec: data })
  }


const handleRest = () => {
    const reset = {
      url: null,
      blob: null,
      chunks: null,
      duration: {
        h: 0,
        m: 0,
        s: 0
      }
    };
    useEffect(()=> { rec: reset })
  }

  return (
    <div>
    <Recorder
    record={true}
    audioURL={rec.audioDetails.url}
    showUIAudio
    handleAudioStop={data => handleAudioStop(data)}
    handleRest={() => handleRest()} 
    />
  </div>


  )
}
